import React, { useState, useEffect } from 'react';
import {
  SafeAreaView,
  ScrollView,
  View,
  Text,
  TextInput,
  TouchableOpacity,
  TouchableWithoutFeedback,
  Alert,
  StyleSheet,
  Dimensions,
  Modal,
  StatusBar,
  Platform,
  useColorScheme,
} from 'react-native';
import {Picker} from '@react-native-picker/picker';
import { Dropdown } from 'react-native-element-dropdown';
import AsyncStorage from '@react-native-async-storage/async-storage';
import * as Print from 'expo-print';
import * as Sharing from 'expo-sharing';
import * as FileSystem from 'expo-file-system';
import { Asset } from 'expo-asset';
import * as SplashScreen from 'expo-splash-screen';

// Pre-cache the logo asset at app startup
const logoAsset = Asset.fromModule(require('./assets/studioeclogo.png'));
const base64Logo = "data:image/png;base64,";

const { width, height } = Dimensions.get('window');

const App = () => {
  // Color scheme detection
  const colorScheme = useColorScheme();
  const isDark = colorScheme === 'dark';
  
  // Main app state
  const [currentPage, setCurrentPage] = useState('main'); // 'main' or 'projects'
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);
  const [documentType, setDocumentType] = useState('Invoice');
  const [docTypePrefix, setDocTypePrefix] = useState('INV');
  const [projectType, setProjectType] = useState('RP');
  const [year, setYear] = useState(new Date().getFullYear().toString());
  const [receiptNumber, setReceiptNumber] = useState('01');
  const [billTo, setBillTo] = useState('');
  const [items, setItems] = useState([{ description: '', quantity: '', price: '', tax: '', amount: 0 }]);
  const [notes, setNotes] = useState('');
  
  // Dropdown data
  const [projectItems, setProjectItems] = useState([]);
  const [docTypeItems] = useState([
    {label: 'Invoice', value: 'Invoice'},
    {label: 'Receipt', value: 'Receipt'}
  ]);
  

  // Project management state
  const [showProjectModal, setShowProjectModal] = useState(false);
  const [newProject, setNewProject] = useState({
    ownerName: '',
    projectName: '',
    projectPlace: '',
    projectNumber: '',
    projectType: 'RP'
  });
  
  // New project form picker data
  const [newProjectTypeItems] = useState([
    {label: 'Residential Project (RP)', value: 'RP'},
    {label: 'Commercial Project (CP)', value: 'CP'},
    {label: 'Community Project (COM)', value: 'COM'}
  ]);

  useEffect(() => {
    // Keep splash screen visible
    SplashScreen.preventAutoHideAsync();
    
    // Pre-load logo asset and projects
    const initializeApp = async () => {
      try {
        // Pre-cache logo asset
        await logoAsset.downloadAsync();
        console.log('Logo asset pre-cached successfully');
      } catch (error) {
        console.log('Logo pre-cache failed:', error);
      }
      
      // Load projects
      await loadProjects();
      
      // Hide splash screen after everything is loaded
      SplashScreen.hideAsync();
    };
    
    initializeApp();
  }, []);

  const loadProjects = async () => {
    try {
      const savedProjects = await AsyncStorage.getItem('projects');
      if (savedProjects) {
        const projectsData = JSON.parse(savedProjects);
        
        // Migrate existing projects to include projectType if missing
        const migratedProjects = projectsData.map(project => ({
          ...project,
          projectType: project.projectType || 'RP' // Default to RP if missing
        }));
        
        // Save migrated projects if any changes were made
        const needsMigration = projectsData.some(project => !project.projectType);
        if (needsMigration) {
          await AsyncStorage.setItem('projects', JSON.stringify(migratedProjects));
        }
        
        setProjects(migratedProjects);
        
        // Convert to dropdown format
        const dropdownItems = migratedProjects.map(project => ({
          label: `${project.projectName} - ${project.ownerName}`,
          value: project.id
        }));
        setProjectItems(dropdownItems);
        
        if (migratedProjects.length > 0 && !selectedProject) {
          setSelectedProject(migratedProjects[0]);
          setBillTo(migratedProjects[0].ownerName);
          // Auto-fill project type if available
          if (migratedProjects[0].projectType) {
            setProjectType(migratedProjects[0].projectType);
          }
        }
      } else {
        const defaultProjects = [
        ];
        await AsyncStorage.setItem('projects', JSON.stringify(defaultProjects));
        setProjects(defaultProjects);
        setSelectedProject(defaultProjects[0]);
        setBillTo(defaultProjects[0].ownerName);
        
        // Convert to dropdown format
        const dropdownItems = defaultProjects.map(project => ({
          label: `${project.projectName} - ${project.ownerName}`,
          value: project.id
        }));
        setProjectItems(dropdownItems);
      }
    } catch (error) {
      console.error('Error loading projects:', error);
    }
  };

  const saveProjects = async (projectsData) => {
    try {
      await AsyncStorage.setItem('projects', JSON.stringify(projectsData));
      setProjects(projectsData);
      
      // Update dropdown items
      const dropdownItems = projectsData.map(project => ({
        label: `${project.projectName} - ${project.ownerName}`,
        value: project.id
      }));
      setProjectItems(dropdownItems);
    } catch (error) {
      console.error('Error saving projects:', error);
    }
  };

  const addProject = async () => {
    if (!newProject.ownerName || !newProject.projectName || !newProject.projectNumber) {
      Alert.alert('Error', 'Please fill in all required fields');
      return;
    }

    const newProjectData = {
      ...newProject,
      id: Date.now().toString()
    };

    const updatedProjects = [...projects, newProjectData];
    await saveProjects(updatedProjects);
    
    setNewProject({
      ownerName: '',
      projectName: '',
      projectPlace: '',
      projectNumber: '',
      projectType: 'RP'
    });
    setShowProjectModal(false);
    Alert.alert('Success', 'Project added successfully!');
  };

  const removeProject = async (projectId) => {
    Alert.alert(
      'Delete Project',
      'Are you sure you want to delete this project?',
      [
        { text: 'Cancel', style: 'cancel' },
        {
          text: 'Delete',
          style: 'destructive',
          onPress: async () => {
            const updatedProjects = projects.filter(p => p.id !== projectId);
            await saveProjects(updatedProjects);
            
            // Update selected project if it was deleted
            if (selectedProject?.id === projectId) {
              if (updatedProjects.length > 0) {
                setSelectedProject(updatedProjects[0]);
                setBillTo(updatedProjects[0].ownerName);
              } else {
                setSelectedProject(null);
                setBillTo('');
              }
            }
            Alert.alert('Success', 'Project deleted successfully!');
          }
        }
      ]
    );
  };

  const calculateAmount = (quantity, price, tax) => {
    const qty = parseFloat(quantity) || 0;
    const prc = parseFloat(price) || 0;
    const taxAmt = parseFloat(tax) || 0;
    return (qty * prc) + taxAmt;
  };

  const updateItem = (index, field, value) => {
    const newItems = [...items];
    newItems[index][field] = value;
    
    if (field === 'quantity' || field === 'price' || field === 'tax') {
      newItems[index].amount = calculateAmount(
        newItems[index].quantity,
        newItems[index].price,
        newItems[index].tax
      );
    }
    
    setItems(newItems);
  };

  const addItem = () => {
    setItems([...items, { description: '', quantity: '', price: '', tax: '', amount: 0 }]);
  };

  const removeItem = (index) => {
    if (items.length > 1) {
      const newItems = items.filter((_, i) => i !== index);
      setItems(newItems);
    }
  };

  const calculateTotal = () => {
    return items.reduce((total, item) => total + item.amount, 0);
  };

  const generateInvoiceNumber = () => {
    const projectNum = selectedProject?.projectNumber || '000';
    return `${docTypePrefix}/${projectType}/${year}/${projectNum}/${receiptNumber}`;
  };

  // Helper function to load logo reliably - Using embedded base64
  const loadLogoBase64 = async () => {
    console.log('✅ Using embedded base64 logo');
    return base64Logo; // Use the embedded base64 string directly - 100% guaranteed to work!
  };

  const generateHTML = async () => {
    const currentDate = new Date().toLocaleDateString('en-GB');
    const invoiceNumber = generateInvoiceNumber();
    const total = calculateTotal();
    
    // Get the logo as base64 - improved for built APKs
    const logoBase64 = await loadLogoBase64();

    // Debug logo status
    console.log('Logo status for PDF:', {
      hasLogo: !!logoBase64,
      logoLength: logoBase64.length,
      startsWithData: logoBase64.startsWith('data:image/')
    });

    const itemsHTML = items.map((item, index) => `
      <tr>
        <td style="padding: 8px; text-align: left;">${String(index + 1).padStart(2, '0')}</td>
        <td style="padding: 8px; text-align: left;">${item.description}</td>
        <td style="padding: 8px; text-align: center;">${item.quantity || '-'}</td>
        <td style="padding: 8px; text-align: center;">${item.price || '-'}</td>
        <td style="padding: 8px; text-align: center;">${item.tax || '-'}</td>
        <td style="padding: 8px; text-align: right;">₹ ${item.amount.toFixed(0)}</td>
      </tr>
    `).join('');

    return `
      <!DOCTYPE html>
      <html>
      <head>
        <meta charset="utf-8">
        <style>
          html, body { 
            margin: 0; 
            padding: 0; 
            font-family: 'Satoshi', -apple-system, BlinkMacSystemFont, sans-serif;
          }
          
          body {
            padding: 10px;
          }
          
          .header { 
            display: flex; 
            justify-content: space-between; 
            align-items: center;
            margin-bottom: 15px;
            margin-left: 0;
          }
          .company-info {
            margin-left: -5px;
          }
          .company-logo { 
            height: 130px; 
            width: auto;
            margin-left: -8px;
          }
          .architect-info { text-align: right; }
          .architect-info h2 { margin: 0; font-size: 16px; font-weight: 500; }
          .architect-info p { margin: 1px 0; font-size: 14px; color: #666; }
          .invoice-header { 
            display: flex; 
            justify-content: space-between; 
            align-items: center;
            margin: 15px 0 25px 0;
            margin-left: 0;
          }
          .invoice-title h2 { font-size: 28px; margin: 0; font-weight: 400; }
          .invoice-number p { font-size: 16px; color: #333; margin: 0; }
          .invoice-details { 
            margin: 15px 0 25px 0;
            margin-left: 0;
          }
          .invoice-details p { margin: 4px 0; font-size: 15px; }
          .items-table { 
            width: 100%; 
            border-collapse: collapse; 
            margin: 15px 0;
            margin-left: 0;
          }
          .items-table th { padding: 10px 6px; text-align: left; border-bottom: 2px solid #333; font-weight: bold; font-size: 14px; }
          .items-table td { padding: 6px; font-weight: bold; font-size: 14px; }
          .total-row { 
            font-weight: bold; 
            font-size: 16px; 
            text-align: right; 
            margin: 15px 0;
            margin-left: 0;
          }
          .notes {
            border-radius: 4px;
            margin: 10px 0;
            padding: 8px;
            font-size: 13px;
            margin-left: 0;
          }
          .signature { 
            margin: 20px 0 10px 0;
            margin-left: 0;
          }
          .signature p { margin: 3px 0; }
          
          .footer { 
            margin-top: 40px;
            font-size: 10px;
            color: #666;
            text-align: center;
            background: white;
            padding: 0;
          }
          
          .footer p {
            margin: 0;
            line-height: 1.4;
          }
          
          @page {
            size: A4;
//            margin: 15px;
            margin: 5mm 10mm 25mm 10mm
          }
          
          .page-container {
            position: relative;
            height: 100vh;
            max-height: 850px;
            display: flex;
            flex-direction: column;
          }
          
          .content {
            flex: 1;
            display: flex;
            flex-direction: column;
            padding: 0;
            padding-bottom: 10px;
          }
          
          .items-section {
            flex: 1;
            min-height: 0;
          }
          
//          .page-footer {
//            position: absolute;
//            bottom: -20%;
//            font-size: 10px;
//            color: #666;
//            text-align: center;
//          }
            .page-footer {
              position: fixed;
              bottom: 0;
              left: 0;
              width: 100%;
              font-size: 10px;
              color: #666;
              text-align: center;
            }
        </style>
      </head>
      <body>
        <div class="page-container">
          <div class="content">
            <div class="header">
              <div class="company-info">
                ${logoBase64 && logoBase64.length > 100 ? 
                  `<img src="${logoBase64}" alt="Studio EC Logo" class="company-logo" style="max-width: 200px; height: auto;" />` : 
                  `<div style="height: 130px; width: 200px; background: linear-gradient(135deg, #6366F1, #8B5CF6); border-radius: 8px; display: flex; align-items: center; justify-content: center; color: white; font-size: 18px; font-weight: bold; text-align: center; margin-left: -8px;">
                    <div>
                      <div style="font-size: 24px; margin-bottom: 8px;">STUDIO EC</div>
                      <div style="font-size: 14px; opacity: 0.9;">Architecture</div>
                    </div>
                  </div>`
                }
              </div>
              <div class="architect-info">
                <h2>Ahamed Afsal EC</h2>
                <p>Architect</p>
              </div>
            </div>

            <div class="invoice-header">
              <div class="invoice-title">
                <h2>${documentType}</h2>
              </div>
              <div class="invoice-number">
               <p>${documentType === "INV" ? "Inv" : "Rec"} : ${invoiceNumber}</p>
              </div>
            </div>

            <div class="invoice-details">
              <p>Date: ${currentDate}</p>
              <p>Bill to: ${billTo}</p>
              <p>Project: ${selectedProject?.projectName || ''}</p>
            </div>

            <div class="items-section">
              <table class="items-table">
                <thead>
                  <tr>
                    <th>Item</th>
                    <th>Description</th>
                    <th>Quantity</th>
                    <th>Price</th>
                    <th>Tax</th>
                    <th>Amount</th>
                  </tr>
                </thead>
                <tbody>
                  ${itemsHTML}
                </tbody>
              </table>

              <div class="total-row">
                <p><strong>Total : ₹ ${total.toLocaleString()}</strong></p>
              </div>

              ${notes ? `<div class="notes"><strong>Notes: </strong>${notes}</div>` : ''}
            </div>

            <div class="signature">
              <p>Regards,</p>
              <p>Ahamed Afsal EC</p>
              <p>Architect</p>
            </div>
          </div>
          
          <div class="page-footer">
            <span>Big Bazaar, Old road, Down Hill Malappuram, Kerala, India - 676519 • +91 8089128946 • +91 9447438431 • studio.ec.in@gmail.com</span>
          </div>
        </div>
      </body>
      </html>
    `;
  };

  const generatePDF = async () => {
    try {
      if (!selectedProject) {
        Alert.alert('Error', 'Please select a project first');
        return;
      }

      if (items.length === 0 || !items[0].description) {
        Alert.alert('Error', 'Please add at least one item');
        return;
      }

      const html = await generateHTML();
      
      // Generate PDF with expo-print (improved settings)
      const { uri } = await Print.printToFileAsync({ 
        html,
        width: 595,
        height: 842,
        margins: {
          left: 20,
          top: 20,
          right: 20,
          bottom: 20,
        }
      });
      
      // Create a clean filename without special characters
      const invoiceNum = generateInvoiceNumber().replace(/\//g, '_');
      const fileName = `${documentType}_${invoiceNum}.pdf`;
      const documentsDir = FileSystem.documentDirectory;
      const newUri = documentsDir + fileName;
      
      // Copy file to documents with proper name
      await FileSystem.copyAsync({
        from: uri,
        to: newUri,
      });

      // Share the PDF
      if (await Sharing.isAvailableAsync()) {
        await Sharing.shareAsync(newUri, {
          mimeType: 'application/pdf',
          dialogTitle: 'Share Invoice/Receipt',
        });
        // Alert.alert('Success', 'PDF generated and ready to share!');
      } else {
        Alert.alert('Success', `PDF generated successfully!\nSaved to: Documents/${fileName}`);
      }

      // Clear all fields to default state after successful generation
      clearAllFields();
    } catch (error) {
      console.error('PDF generation error:', error);
      Alert.alert('Error', 'Failed to generate PDF. Please try again.\nError: ' + error.message);
    }
  };

  // Clear all fields to default state
  const clearAllFields = () => {
    // Reset document fields
    setDocumentType('Invoice');
    setDocTypePrefix('INV');
    
    // Reset form fields but keep selected project
    setYear(new Date().getFullYear().toString());
    setReceiptNumber('01');
    setBillTo('');
    setItems([{ description: '', quantity: '', price: '', tax: '', amount: 0 }]);
    setNotes('');
  };

  // Project Management Page
  const renderProjectManagement = () => (
    <View style={dynamicStyles.pageContainer}>
      <SafeAreaView style={dynamicStyles.safeArea}>
        <View style={dynamicStyles.header}>
          <TouchableOpacity 
            onPress={() => {
              setCurrentPage('main');
            }} 
            style={dynamicStyles.modernBackButton}
            activeOpacity={0.7}
            hitSlop={{ top: 15, bottom: 15, left: 15, right: 15 }}
          >
            <Text style={dynamicStyles.modernBackButtonIcon}>‹</Text>
            <Text style={dynamicStyles.modernBackButtonText}>Back</Text>
          </TouchableOpacity>
          <Text style={dynamicStyles.pageTitle}>Manage Projects</Text>
        </View>

      <TouchableOpacity 
        onPress={() => setShowProjectModal(true)} 
        style={dynamicStyles.addProjectButton}
      >
        <Text style={dynamicStyles.addProjectButtonText}>+ Add New Project</Text>
      </TouchableOpacity>

      <ScrollView style={dynamicStyles.projectsList}>
        {projects.map((project) => (
          <View key={project.id} style={dynamicStyles.projectItem}>
            <View style={dynamicStyles.projectInfo}>
              <Text style={dynamicStyles.projectTitle}>{project.projectName}</Text>
              <Text style={dynamicStyles.projectSubtitle}>Owner: {project.ownerName}</Text>
              <Text style={dynamicStyles.projectSubtitle}>Place: {project.projectPlace}</Text>
              <Text style={dynamicStyles.projectSubtitle}>Project #: {project.projectNumber}</Text>
              <Text style={dynamicStyles.projectSubtitle}>Type: {project.projectType === 'RP' ? 'Residential' : project.projectType === 'CP' ? 'Commercial' : 'Community'}</Text>
            </View>
            <TouchableOpacity 
              onPress={() => removeProject(project.id)} 
              style={dynamicStyles.deleteButton}
            >
              <Text style={dynamicStyles.deleteButtonText}>Delete</Text>
            </TouchableOpacity>
          </View>
        ))}
      </ScrollView>

      {/* Add Project Modal */}
      <Modal
        visible={showProjectModal}
        transparent={true}
        animationType="slide"
      >
        <View style={dynamicStyles.modalContainer}>
          <View style={dynamicStyles.modalContent}>
            <Text style={dynamicStyles.modalTitle}>Add New Project</Text>

            <Text style={dynamicStyles.label}>Project Type *</Text>
            <Dropdown
              style={[dynamicStyles.elementDropdown, {marginBottom: 16}]}
              placeholderStyle={dynamicStyles.elementPlaceholderText}
              selectedTextStyle={dynamicStyles.elementSelectedText}
              containerStyle={dynamicStyles.elementDropdownContainer}
              itemTextStyle={dynamicStyles.elementItemText}
            itemContainerStyle={dynamicStyles.elementItemContainer}
              itemContainerStyle={dynamicStyles.elementItemContainer}
              activeColor={isDark ? '#333' : '#f0f0f0'}
              data={newProjectTypeItems}
              maxHeight={300}
              labelField="label"
              valueField="value"
              placeholderTextColor={isDark ? '#888' : '#999'}
              placeholder="Select project type..."
              value={newProject.projectType || 'RP'}
              onChange={(item) => {
                setNewProject({...newProject, projectType: item.value});
              }}
              renderRightIcon={() => (
                <Text style={dynamicStyles.elementDropdownIcon}>▼</Text>
              )}
            />

            <TextInput
              style={dynamicStyles.input}
              placeholderTextColor={isDark ? '#888' : '#999'}
              placeholder="Project Number * (e.g., 010)"
              placeholderTextColor={isDark ? '#888' : '#999'}
              value={newProject.projectNumber}
              onChangeText={(text) => setNewProject({...newProject, projectNumber: text})}
              keyboardType="numeric"
            />

            <TextInput
              style={dynamicStyles.input}
              placeholderTextColor={isDark ? '#888' : '#999'}
              placeholder="Project Name *"
              value={newProject.projectName}
              onChangeText={(text) => setNewProject({...newProject, projectName: text})}
            />
            
            <TextInput
              style={dynamicStyles.input}
              placeholderTextColor={isDark ? '#888' : '#999'}
              placeholder="Project Owner Name *"
              value={newProject.ownerName}
              onChangeText={(text) => setNewProject({...newProject, ownerName: text})}
            />
            
            <TextInput
              style={dynamicStyles.input}
              placeholderTextColor={isDark ? '#888' : '#999'}
              placeholder="Project Place"
              value={newProject.projectPlace}
              onChangeText={(text) => setNewProject({...newProject, projectPlace: text})}
            />

            <View style={dynamicStyles.modalButtons}>
              <TouchableOpacity 
                onPress={() => setShowProjectModal(false)} 
                style={[dynamicStyles.modalButton, dynamicStyles.cancelButton]}
              >
                <Text style={dynamicStyles.cancelButtonText}>Cancel</Text>
              </TouchableOpacity>
              
              <TouchableOpacity 
                onPress={addProject} 
                style={[dynamicStyles.modalButton, dynamicStyles.saveButton]}
              >
                <Text style={dynamicStyles.saveButtonText}>Add Project</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>
      </SafeAreaView>
    </View>
  );

  // Function to close all dropdowns - no longer needed with Element Dropdown
  // const closeAllDropdowns = () => {
  //   setShowProjectDropdown(false);
  //   setShowDocTypeDropdown(false);
  //   setShowNewProjectTypeDropdown(false);
  // };

  // Main Generator Page
  const renderMainPage = () => (
    <SafeAreaView style={dynamicStyles.container}>
      <ScrollView style={dynamicStyles.scrollView} contentContainerStyle={dynamicStyles.contentContainer}>
        <Text style={dynamicStyles.title}>Receipt/Invoice Generator</Text>
        
        <TouchableOpacity 
          onPress={() => setCurrentPage('projects')} 
          style={dynamicStyles.manageProjectsButton}
        >
          <Text style={dynamicStyles.manageProjectsButtonText}>Manage Projects</Text>
        </TouchableOpacity>

        {/* Project Selection */}
        <View style={dynamicStyles.section}>
          <Text style={dynamicStyles.sectionTitle}>Project Selection</Text>
          <Dropdown
            style={dynamicStyles.elementDropdown}
            placeholderStyle={dynamicStyles.elementPlaceholderText}
            selectedTextStyle={dynamicStyles.elementSelectedText}
            containerStyle={dynamicStyles.elementDropdownContainer}
            itemTextStyle={dynamicStyles.elementItemText}
            itemContainerStyle={dynamicStyles.elementItemContainer}
            data={[
              { label: 'Select a project...', value: null },
              ...projects.map(project => ({
                label: `${project.projectName} - ${project.ownerName}`,
                value: project.id,
                project: project
              }))
            ]}
            maxHeight={300}
            labelField="label"
            valueField="value"
            placeholder="Select a project..."
            value={selectedProject?.id || null}
            onChange={(item) => {
              if (item.value === null) {
                setSelectedProject(null);
                setBillTo('');
              } else {
                const project = projects.find(p => p.id === item.value);
                if (project) {
                  setSelectedProject(project);
                  setBillTo(project.ownerName);
                  if (project.projectType) {
                    setProjectType(project.projectType);
                  }
                }
              }
            }}
            renderRightIcon={() => (
              <Text style={dynamicStyles.elementDropdownIcon}>▼</Text>
            )}
            activeColor={isDark ? '#333' : '#f0f0f0'}
          />
        </View>

        {/* Document Type */}
        <View style={dynamicStyles.section}>
          <Text style={dynamicStyles.sectionTitle}>Document Type</Text>
          <Dropdown
            style={dynamicStyles.elementDropdown}
            placeholderStyle={dynamicStyles.elementPlaceholderText}
            selectedTextStyle={dynamicStyles.elementSelectedText}
            containerStyle={dynamicStyles.elementDropdownContainer}
            itemTextStyle={dynamicStyles.elementItemText}
            itemContainerStyle={dynamicStyles.elementItemContainer}
            data={docTypeItems}
            maxHeight={300}
            labelField="label"
            valueField="value"
            placeholder="Select document type..."
            value={documentType}
            onChange={(item) => {
              setDocumentType(item.value);
              setDocTypePrefix(item.value === 'Invoice' ? 'INV' : 'REC');
            }}
            renderRightIcon={() => (
              <Text style={dynamicStyles.elementDropdownIcon}>▼</Text>
            )}
            activeColor={isDark ? '#333' : '#f0f0f0'}
          />
        </View>

        {/* Document Number */}
        <View style={dynamicStyles.section}>
          <Text style={dynamicStyles.sectionTitle}>Document Number</Text>
          <Text style={dynamicStyles.documentNumber}>{generateInvoiceNumber()}</Text>
          
          <View style={dynamicStyles.row}>
            <View style={dynamicStyles.halfWidth}>
              <Text style={dynamicStyles.label}>Year</Text>
              <TextInput
                style={dynamicStyles.input}
                value={year}
                onChangeText={setYear}
                keyboardType="numeric"
                maxLength={4}
              />
            </View>
            
            <View style={dynamicStyles.halfWidth}>
              <Text style={dynamicStyles.label}>Receipt Number</Text>
              <TextInput
                style={dynamicStyles.input}
                value={receiptNumber}
                onChangeText={setReceiptNumber}
                keyboardType="numeric"
                placeholderTextColor={isDark ? '#888' : '#999'}
              placeholder="01"
              />
            </View>
          </View>
        </View>

        {/* Basic Information */}
        <View style={dynamicStyles.section}>
          <Text style={dynamicStyles.sectionTitle}>Basic Information</Text>
          <Text style={dynamicStyles.autoFillText}>Date: {new Date().toLocaleDateString('en-GB')}</Text>
          
          <View style={dynamicStyles.inputContainer}>
            <Text style={dynamicStyles.label}>Bill To</Text>
            <TextInput
              style={dynamicStyles.input}
              value={billTo}
              onChangeText={setBillTo}
              placeholderTextColor={isDark ? '#888' : '#999'}
              placeholder="Client Name"
            />
          </View>
          
          <Text style={dynamicStyles.autoFillText}>Project: {selectedProject?.projectName || 'No project selected'}</Text>
        </View>

        {/* Items */}
        <View style={dynamicStyles.section}>
          <Text style={dynamicStyles.sectionTitle}>Items</Text>
          
          {items.map((item, index) => (
            <View key={index} style={dynamicStyles.itemContainer}>
              <View style={dynamicStyles.itemHeader}>
                <Text style={dynamicStyles.itemTitle}>Item {index + 1}</Text>
                {items.length > 1 && (
                  <TouchableOpacity onPress={() => removeItem(index)} style={dynamicStyles.removeButton}>
                    <Text style={dynamicStyles.removeButtonText}>Remove</Text>
                  </TouchableOpacity>
                )}
              </View>
              
              <TextInput
                style={[dynamicStyles.input, dynamicStyles.textArea]}
                value={item.description}
                onChangeText={(value) => updateItem(index, 'description', value)}
                placeholderTextColor={isDark ? '#888' : '#999'}
              placeholder="Description"
                multiline
                numberOfLines={3}
              />
              
              <View style={dynamicStyles.row}>
                <View style={dynamicStyles.quarterWidth}>
                  <Text style={dynamicStyles.label}>Quantity</Text>
                  <TextInput
                    style={dynamicStyles.input}
                    value={item.quantity}
                    onChangeText={(value) => updateItem(index, 'quantity', value)}
                    placeholderTextColor={isDark ? '#888' : '#999'}
              placeholder="0"
                    keyboardType="numeric"
                  />
                </View>
                
                <View style={dynamicStyles.quarterWidth}>
                  <Text style={dynamicStyles.label}>Price (₹)</Text>
                  <TextInput
                    style={dynamicStyles.input}
                    value={item.price}
                    onChangeText={(value) => updateItem(index, 'price', value)}
                    placeholderTextColor={isDark ? '#888' : '#999'}
              placeholder="0"
                    keyboardType="numeric"
                  />
                </View>
                
                <View style={dynamicStyles.quarterWidth}>
                  <Text style={dynamicStyles.label}>Tax (₹)</Text>
                  <TextInput
                    style={dynamicStyles.input}
                    value={item.tax}
                    onChangeText={(value) => updateItem(index, 'tax', value)}
                    placeholderTextColor={isDark ? '#888' : '#999'}
              placeholder="0"
                    keyboardType="numeric"
                  />
                </View>
                
                <View style={dynamicStyles.quarterWidth}>
                  <Text style={dynamicStyles.label}>Amount</Text>
                  <Text style={dynamicStyles.amountText}>₹ {item.amount.toFixed(0)}</Text>
                </View>
              </View>
            </View>
          ))}
          
          <TouchableOpacity onPress={addItem} style={dynamicStyles.addButton}>
            <Text style={dynamicStyles.addButtonText}>Add Item</Text>
          </TouchableOpacity>
          
          <View style={dynamicStyles.totalContainer}>
            <Text style={dynamicStyles.totalText}>Total: ₹ {calculateTotal().toLocaleString()}</Text>
          </View>
        </View>

        {/* Notes */}
        <View style={dynamicStyles.section}>
          <Text style={dynamicStyles.sectionTitle}>Notes</Text>
          <TextInput
            style={[dynamicStyles.input, dynamicStyles.textArea]}
            value={notes}
            onChangeText={setNotes}
            placeholder="Add any additional notes here..."
            multiline
            numberOfLines={4}
          />
        </View>

        {/* Generate PDF Button */}
        <TouchableOpacity onPress={generatePDF} style={dynamicStyles.generateButton}>
          <Text style={dynamicStyles.generateButtonText}>Generate PDF</Text>
        </TouchableOpacity>
      </ScrollView>
    </SafeAreaView>
  );

  // Create dynamic styles based on theme
  const dynamicStyles = createStyles(isDark);
  
  // Main return - switch between pages
  return (
    <>
      <StatusBar 
        barStyle={isDark ? "light-content" : "dark-content"}
        backgroundColor={isDark ? "#1a1a1a" : "#f5f5f5"}
        translucent={false}
      />
      {currentPage === 'projects' ? renderProjectManagement() : renderMainPage()}
    </>
  );
};

